import React from "react";
import { useNavigate } from "react-router-dom";

import axios from "axios";
import { UseTodo } from "../contexts/TodosContext";

const TodoCard = (props) => {
  const { todo } = props;
  let { getTodosData } = UseTodo(); //has value =todos

  const { id, title, completed } = todo;

  const navigate = useNavigate();

  const deleteTodo = (e, id) => {
    e.preventDefault();
    // 192.168.1.54:8000 IP
    axios
      .delete(`https://jsonplaceholder.typicode.com/todos/${id}`)
      .then((res) => {
        console.log("deleted", res);
        getTodosData();
      })
      .catch((err) => console.log(err));
  };

  return (
    <>
      <div
        style={{
          backgroundColor: "grey",
          margin: "10px",
          padding: "15px",
          width: "150px",
        }}
        onClick={() => navigate(`/todos/${id}`)}
      >
        <h4> {title} </h4>
        <h6> {`completed ${completed}`}</h6>

        {/* <button onClick={() => navigate(`/todos/${_id}`)}>view Todo</button> */}
      </div>
      <button onClick={(e) => deleteTodo(e, id)}>
        <i class="fa-solid fa-trash-can"></i>Delete
      </button>
    </>
  );
};

export default TodoCard;
